    private void btnRegistrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnRegistrarActionPerformed
        // TODO add your handling code here:
        java.util.Date fechaN=date.getDate();
            long fecha=fechaN.getTime();
            java.sql.Date dateN = new java.sql.Date(fecha);
        try {
            Connection cn = DriverManager.getConnection("jdbc:mysql://localhost/sic", "root", "6182");
            //localhost es 127.0.0.1
            PreparedStatement pst = cn.prepareStatement("insert into ventas_encabezado values(?,?,?,?,?)");

            pst.setString(1, txtCodigoC.getText().trim());
            pst.setString(2, txtCliente.getText().trim());
            pst.setString(3, dateN.toString());
            pst.setString(4, txtTotal.getText().trim());
            pst.setString(5, txtStatus.getText().trim());

            pst.executeUpdate();
            
            PreparedStatement pst2 = cn.prepareStatement("insert into ventas_detalle values(?,?,?,?,?,?)");
            
            pst.setString(1, txtCodigoC.getText().trim());
            pst.setString(1, txtProducto.getText().trim());
            pst.setString(2, txtCantidad.getText().trim());
            pst.setString(2, txtCosto.getText().trim());
            pst.setString(4, txtPrecio.getText().trim());
            pst.setString(5, txtBodega.getText().trim());

            pst2.executeUpdate();
           
            JOptionPane.showMessageDialog(this, "¡REGISTRO EXITOSO!", "Mensaje", JOptionPane.INFORMATION_MESSAGE);
            txtCodigoC.setText("");
            txtCliente.setText("");
            txtTotal.setText("");
            txtStatus.setText("");
            txtProducto.setText("");
            txtCantidad.setText("");
            txtCosto.setText("");
            txtPrecio.setText("");
            txtBodega.setText("");
                   } catch (SQLException e) {
            JOptionPane.showMessageDialog(this, "Error en registro", "Warning", JOptionPane.WARNING_MESSAGE);
        }
    }//GEN-LAST:event_btnRegistrarActionPerformed

