    private void btnBuscarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnBuscarActionPerformed
        // TODO add your handling code here:
        try {
            Connection cn = DriverManager.getConnection("jdbc:mysql://localhost/sic", "root", "6182");
            PreparedStatement pst = cn.prepareStatement("select * from ventas_encabezado where codigo_ventaenca =?");
            pst.setString(1, txtbuscado.getText().trim());

            ResultSet rs = pst.executeQuery();

            if (rs.next()) {
                txtCliente.setText(rs.getString("codigo_cliente"));
                txtTotal.setText(rs.getString("total_ventaenca"));
                txtStatus.setText(rs.getString("etatus_ventaenca"));

              

            } else {
                JOptionPane.showMessageDialog(null, "Vendedor no registrado.");
            }
            
            PreparedStatement pst2 = cn.prepareStatement("select * from ventas_detalle where codigo_ventaenca =?");
            pst2.setString(1, txtbuscado.getText().trim());
            ResultSet rs2 = pst2.executeQuery();

            if (rs2.next()) {
                txtCantidad.setText(rs.getString("cantidad_ventadet"));
                txtCosto.setText(rs.getString("costo_ventadet"));
                txtPrecio.setText(rs.getString("precio_ventadet"));
                txtBodega.setText(rs.getString("codigo_bodega"));
              

            } else {
                JOptionPane.showMessageDialog(null, "Vendedor no registrado.");
            }

        } catch (HeadlessException | SQLException e) {
            JOptionPane.showMessageDialog(this, "Error en Seleccion", "Warning", JOptionPane.WARNING_MESSAGE);
        }
    }//GEN-LAST:event_btnBuscarActionPerformed

    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        int cantidad=Integer.valueOf(txtCantidad.getText());
        int precio=Integer.valueOf(txtPrecio.getText());
        int costo=cantidad*precio;
        String c;
        c=costo+"";
        txtCosto.setText(c);
        txtTotal.setText(c);
        
    }//GEN-LAST:event_jButton1ActionPerformed


